import React ,{Component}from 'react';
import './JobDetailView.css';
import { MDBIcon } from "mdbreact";
class JobDetailView extends Component{
    jobListView=()=>{
        this.props.history.push({
            pathname:'/'              
        }) 
    }
    render(){
        return (
            <div>
                 <button onClick ={this.jobListView}>gotoJobsList</button> 
                <div className = "JobDetailHeader">
                    <div>
                        <h3 >Vesa</h3>
                        <span>Young Talent</span>
                    </div>
                    <div>
                        <div>
                            <span><MDBIcon icon="map-marker-alt" /></span>
                            <span>Hyderabad</span>
                        </div>
                        <div>
                            <span><MDBIcon icon="graduation-cap" /></span>
                            <span>ME/M.Tech(Civil), BE/B.Tech</span>
                        </div>
                        <div>
                            <span><MDBIcon icon="rupee-sign" /></span>
                            <span>8000 - 20000 Monthly</span>
                        </div>
                    </div>
                    <div>
                   {/* <div className = "JobDetailView-align"> */}
                       <span>Last Date 19 OCT 2020</span>
                       <button type="button" style ={{  padding: '8px',height: "27px",lineHeight: '1px',
                            fontSize: '14px',textTransform:'none'}}className = "btn btn-amber">Apply Now</button>
                       {/* <MDBBtn color="amber">Amber</MDBBtn> */}
                   {/* </div>  */}
                </div>
                </div>
               
                
            </div>
        )
    }
}
export default JobDetailView
